import { table,minifyRecord } from './utils/Airtable.js';
import auth0 from './utils/auth0';
import OwnsRecord from './middleware/OwnsRecord.js';

    const handler = async (req, res) => {
        const { user } = await auth0.getSession(req);
    const { id,fields } = req.body;
    try {
        const newFields = { ...fields, userId: user.sub };
        const updatedRecord = await table.update([
            { id,fields:newFields },
        ]);
       
        res.statusCode = 200;
        res.json(minifyRecord(updatedRecord));
    } catch (error) {
        console.error(error);
        res.statusCode = 500;
        res.json({ msg: 'Something went wrong' });
    }
}


export default auth0.requireAuthentication(OwnsRecord(handler));
