import React, { useState, useContext } from 'react';
import { SubsContext } from '../context/SubsContext';
import Tags from './Tags'


export default function SubsForm() {
  
    const [subs,setSubs]=useState({
        name:'',
        url:'',
        amount:'',
        date:'',
        edate:'',
        trial:[]
        

    })
    const [count,setCount]=useState(0)
    const { addSubs } = useContext(SubsContext);
    
    const handleSubmit = (e) => {
        e.preventDefault();
        
        addSubs(subs)
        setSubs({
            ...subs,
            [e.target.name]: e.target.value
          });


        
        setCount(count + 1);
    };
    return (
        <form className="form my-6" onSubmit={handleSubmit}>
            <div className="flex flex-col text-sm mb-2">
                <label htmlFor="name" className="font-bold mb-2 text-gray-800 ">
                    Add a Subscription
                </label>
                
                <input
                    type="text"
                    name="name"
                    value={subs.name}
                   
                    className=" appearance-none border border-gray-200 p-2 focus:outline-none focus:border-gray-500 rounded-lg "
                    onChange={(e) => setName(e.target.value)}
                />
                <label htmlFor="date" className="font-bold mb-2 text-gray-800 ">
                    Date of Purchase
                </label>
                <input
                    type="date"
                    name="date"
                    value={subs.date}
                   
                    className=" appearance-none border border-gray-200 p-2 focus:outline-none focus:border-gray-500 rounded-lg "
                    onChange={(e) => setDate(e.target.value)}
                />
                <label htmlFor="subscription" className="font-bold mb-2 text-gray-800 ">
                    Date of Renewal
                </label>
                <input
                    type="date"
                    name="Date of Renewal"
                    value={subs.edate}
                   
                    className=" appearance-none border border-gray-200 p-2 focus:outline-none focus:border-gray-500 rounded-lg "
                    onChange={(e) => setEdate(e.target.value)}
                />
                <label htmlFor="subscription" className="font-bold mb-2 text-gray-800 ">
                   Link
                </label>
               
                <input
                    type="text"
                    name="Link"
                    value={subs.url}
                   
                    className=" appearance-none border border-gray-200 p-2 focus:outline-none focus:border-gray-500 rounded-lg "
                    onChange={(e) => setUrl(e.target.value)}
                />
                
                <label htmlFor="subscription" className="font-bold mb-2 text-gray-800 ">
                    Free-Trial
                </label>
                <Tags tagsUpdated={setTrial} key={count} />
                <label htmlFor="subscription" className="font-bold mb-2 text-gray-800 ">
                    Amount
                </label>
                <input
                    type="text"
                    name="subscription"
                    value={subs.amount}
                   
                    className=" appearance-none border border-gray-200 p-2 focus:outline-none focus:border-gray-500 rounded-lg "
                    onChange={(e) => setAmount(e.target.value)}
                />
            </div>
            <button
                type="submit"
                className=" w-full rounded bg-blue-500 hover:bg-blue-600 text-white py-2 px-4"
            >
                Submit
            </button>
        </form>
    );
}
